import React from 'react'
import ReactDOM from 'react-dom'

const Header = () =>{
  const heading = "Half Stack Application developement"
  return(
  <div>
    <h1>{heading}</h1>
  </div>
  )
}

const Content = (props) =>{
console.log(props)
return(
    <div>
      <p>{props.name} {props.exer}</p>
    </div>
  )
}

const Total = () =>{
  const total = 31
  return(
    <div>
      <p>Number of exercises {total}</p>
    </div>
  )
}

const App = () => {
    const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  return (
    <div>
      <Header />
      <Content name = {parts[0].name} exer = {parts[0].exercises} />
      <Content name = {parts[1].name} exer = {parts[1].exercises} />
      <Content name = {parts[2].name} exer = {parts[2].exercises} />
      <Total />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
