import React, { useState } from 'react'
import Phonebook from './Phonebook'

const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas'}
  ]) 
  const [ newName, setNewName ] = useState('')

  const addNumber=(event)=>{
    event.preventDefault()
    const phoneObject = {
      name: newName
    }
    console.log(persons.some(person=>person.name===newName))
    if(persons.includes(newName)){    
      window.alert('This name already exists')}
    else{
      setPersons(persons.concat(phoneObject))
      setNewName('')
    }
   
  
  }

  const handleNumber=(event)=>{
    setNewName(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addNumber}>
        <div>
          name: <input value={newName} onChange={handleNumber}/>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map(persons=>
        <Phonebook name={persons.name} key={persons.name}/>)}
    </div>
  )
}

export default App
